import React from "react";
import { useState } from "react";
import { useEffect } from "react";

import firebase from "firebase";
import TextField from "@material-ui/core/TextField";
import { Button, Container } from "@material-ui/core";
import { db } from "./firebase_config";
import TodoListItem from "./TodoList";

export default function TodoComp(user) {
  //  checking user auth
  const [isSignedIn, setIsSignedIn] = useState(false); // Local signed-in state.
  const userdata = firebase.auth().currentUser;

  // Listen to the Firebase Auth state and set the local state.
  useEffect(() => {
    const unregisterAuthObserver = firebase
      .auth()
      .onAuthStateChanged((user) => {
        setIsSignedIn(!!user);
      });
    return () => unregisterAuthObserver(); // Make sure we un-register Firebase observers when the component unmounts.
  }, []);

  const [todo, settodo] = useState("");

  const [todoList, settodoList] = useState([]);
  const [mytodolist, setMytodolist] = useState([]);
  useEffect(() => {
    gettodos();
  }, []);

  const inputChangeHandler = (event) => {
    settodo(event.target.value);
  };

  const gettodos = () => {
    db.collection("todos").onSnapshot(function (querySnapshot) {
      settodoList(
        querySnapshot.docs.map((doc) => ({
          id: doc.id,
          todo: doc.data().todo,
          isprogress: doc.data().isprogress,
        }))
      );
    });
  };
  const addToDo = (event) => {
    event.preventDefault();
    db.collection("mytodos").doc(userdata.uid).set({
      isprogress: true,
      timestamp: firebase.firestore.FieldValue.serverTimestamp(),
      todo: todo,
    });
    db.collection("todos").add({
      isprogress: true,
      timestamp: firebase.firestore.FieldValue.serverTimestamp(),
      todo: todo,
    });

    settodo("");
  };
  if (isSignedIn) {
    return (
      <div className="App">
        <h1>To-Do-Da - Write your todo</h1>
        <TextField
          id="filled-basic"
          label="Create Your Todo"
          value={todo}
          variant="filled"
          style={{ width: "50%", backgroundColor: "#ffffff" }}
          onChange={inputChangeHandler}
          // onKeyDown={addToDo}
        />
        <Button
          disabled={todo === ""}
          type="submit"
          onClick={addToDo}
          variant="contained"
          color="primary"
          style={{
            padding: "0.9rem 2rem",
            fontSize: "0.9rem",
            marginLeft: "1rem",
          }}
        >
          Add
        </Button>

        <Container>
          {todoList.map((e) => (
            <TodoListItem todo={e.todo} isprogress={e.isprogress} id={e.id} />
          ))}
        </Container>
      </div>
    );
  } else {
    return <h1>You're not Signed in</h1>;
  }
}
