import { useState } from "react";
import { useEffect } from "react";
import "./App.css";
import firebase from "firebase";
import TextField from "@material-ui/core/TextField";
import {
  Button,
  Container,
  createMuiTheme,
  ThemeProvider,
} from "@material-ui/core";
import { db } from "./firebase_config";
import TodoListItem from "./TodoList";
// import DarkModeButton from "./DarkMode";

function App() {
  const theme = createMuiTheme({
    palette: {
      type: "dark",
    },
  });

  const [todo, settodo] = useState("");

  const [todoList, settodoList] = useState([]);

  useEffect(() => {
    gettodos();
  }, []);

  const inputChangeHandler = (event) => {
    settodo(event.target.value);
  };

  const gettodos = () => {
    db.collection("todos").onSnapshot(function (querySnapshot) {
      settodoList(
        querySnapshot.docs.map((doc) => ({
          id: doc.id,
          todo: doc.data().todo,
          isprogress: doc.data().isprogress,
        }))
      );
    });
  };
  const addToDo = (event) => {
    event.preventDefault();
    db.collection("todos").add({
      isprogress: true,
      timestamp: firebase.firestore.FieldValue.serverTimestamp(),
      todo: todo,
    });

    settodo("");
  };
  // console.log(todoList);
  return (
    <ThemeProvider>
      <div className="App">
        {/* <DarkModeButton /> */}
        <h1>To-Do-Da - Write your todo</h1>
        <TextField
          id="filled-basic"
          label="Create Your Todo"
          value={todo}
          variant="filled"
          style={{ width: "50%", backgroundColor: "#ffffff" }}
          onChange={inputChangeHandler}
          // onKeyDown={addToDo}
        />
        <Button
          disabled={todo === ""}
          type="submit"
          onClick={addToDo}
          variant="contained"
          color="primary"
          style={{
            padding: "0.9rem 2rem",
            fontSize: "0.9rem",
            marginLeft: "1rem",
          }}
        >
          Add
        </Button>

        <Container>
          {todoList.map((e) => (
            <TodoListItem todo={e.todo} isprogress={e.isprogress} id={e.id} />
          ))}
        </Container>
      </div>
    </ThemeProvider>
  );
}

export default App;
