import { Button, Container, ListItem, ListItemText } from "@material-ui/core";
import React from "react";
import { db } from "./firebase_config";
export default function TodoList({ todo, isprogress, id, userdata }) {
  function toggleIsProgress() {
    db.collection("todos").doc(id).update({
      isprogress: !isprogress,
    });
    function deleteTodo() {
      db.collection("todos").doc(id).delete();
    }

    return (
      <>
        <Container style={{ width: "90%", marginTop: "2.5rem" }}>
          <ListItem>
            <ListItemText
              primary={todo}
              secondary={isprogress ? "In Progress" : "Tast Completed"}
            />
            <Button onClick={toggleIsProgress}>
              {isprogress ? "Done" : "Undone"}
            </Button>
            <Button onClick={deleteTodo}>X</Button>
          </ListItem>
        </Container>
      </>
    );
  }
}
